import React, { Component } from 'react';
import { List, Avatar, Icon } from 'antd'
import axios from "axios";
import TopicInfo from "./TopicInfo";

const IconText = ({ type, text }) => (
    <span>
    <Icon type={type} style={{ marginRight: 8 }} />
        {text}
  </span>
);

class HotMessageTopic extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            topics :[],
            topicDrawerVisible:false,
            focusId: null,
            oneTopicinfo:[],
            comments:[],
            title:null
        }
    }

    componentWillMount(){
        this.getAllTopic()
    }

    getTopicInfo(topicid){

        let _this = this;
        console.log("topicid=", _this.state.focusId)
        axios.get("http://127.0.0.1:8080/getonetopicinfo",{
            params:{
                topicid: topicid
            }
        }).then(function (response) {
            _this.setState({
                title:response.data.title
            });
            console.log(response.data)
        }).catch(function (error) {
            console.log(error)
        })
    }

    getTopicComments(topicid){

        let _this = this;
        axios.get("http://127.0.0.1:8080/gettopiccomments",{
            params:{
                topicid: topicid
            }
        }).then(function (response) {
            _this.setState({
                comments: response.data
            });
            console.log(response.data)
        })
    }

    getAllTopic(){
        let _this = this;
        axios.get("http://127.0.0.1:8080/getAllTopics")
            .then(function (reponse) {
                _this.setState({
                    topics:reponse.data
                });
                console.log("topics2",_this.state.topics);
                console.log(reponse.data)
            })
    }

    openOneTopic(id){

        console.log("id = ", id);
        this.setState({
            topicDrawerVisible: true,
            focusId: id
        });
        this.getTopicInfo(id);
        this.getTopicComments(id)
    }

    closeTopicDrawer(topicDrawerVisible){
        this.setState({
            topicDrawerVisible
        })
    }

    render() {
        return (
            <div>
                <List
                    itemLayout="vertical"
                    dataSource={this.state.topics}
                    size="middle"
                    pagination={{
                        pageSize:4
                    }}
                    renderItem={item => (
                        <List.Item
                            key = {item.id}
                            actions={[<IconText type='star-o' text='0'/>, <IconText type='like-o' text='0'/>, <IconText type='message' text='0'/>]}
                        >
                            <List.Item.Meta
                                title={<a onClick={()=> this.openOneTopic(item.id)}>{item.title}</a>}
                                description = {[<Avatar
                                    shape={"square"}
                                    size={"small"}
                                    src={"https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"} />,
                                    item.username,
                                    ,"        ",item.time]}/>
                            {item.content}
                        </List.Item>
                    )}/>
                <TopicInfo
                    topicDrawerVisible = {this.state.topicDrawerVisible}
                    closeTopicDrawer = { topicDrawerVisible => this.closeTopicDrawer(topicDrawerVisible)}
                    topicinfo = {this.state.oneTopicinfo}
                    comments = {this.state.comments}
                    title = {this.state.title}
                />
            </div>
        );
    }
}
export default HotMessageTopic;
