import React, { Component } from 'react';
import {Drawer} from 'antd';
import axios from "axios";


class TopicInfo extends React.Component{

    constructor(props){

        super(props);
        this.state = {
            topicinfo: [],
            comments:[],
            topicid:this.props.topicid
        }
    }

    getTopicInfo(topicid){

        let _this = this;
        console.log("topicid=", _this.state.focusId)
        axios.get("http://127.0.0.1:8080/getonetopicinfo",{
            params:{
                topicid: topicid
            }
        }).then(function (response) {
            _this.setState({
                topicinfo:response.data
            });
            console.log(response.data)
        }).catch(function (error) {
            console.log(error)
        })
    }

    getTopicComments(topicid){

        let _this = this;
        axios.get("http://127.0.0.1:8080/gettopiccomments",{
            params:{
                topicid: topicid
            }
        }).then(function (response) {
            _this.setState({
                comments: response.data
            });
            console.log(response.data)
        })
    }

    componentWillReceiveProps(nextProps){
        if (this.state.topicid !== nextProps.topicid){
            this.setState({
                topicid: nextProps.topicid
            },() => {
                this.getTopicInfo(nextProps.topicid);
                this.getTopicComments(nextProps.topicid)
            })
            console.log("topicinfo=", this.state.topicinfo);
            console.log("comments:", this.state.comments)
        }
    }


        render() {

        return (
            <div>
                <Drawer
                    width={600}
                    placement={"right"}
                    visible={this.props.topicDrawerVisible}
                    onClose={() => this.props.closeTopicDrawer(false)}
                    title={this.state.title}
                    >
                    {this.state.title}
                </Drawer>
            </div>
        );
    }
}

export default TopicInfo;
