import React from "react";
import { BrowserRouter as Router, Route, Link, Switch, Redirect } from "react-router-dom";
import PropTypes from 'prop-types';
import {Row, Layout,Menu, Breadcrumb, Icon, Button, Popover} from 'antd'
import App from "./App";
import './homepage.css'
import MostHotTopic from "./MostHotTopic";
import HotMessageTopic from "./HotMessageTopic";
import AllTopic from "./AllTopic";
import Involved from "./Involved";
import Visitor from "./Visitor";
import Auth from "./login/auth";
import LoginForm from "./login/login"

/**
 *created by Chaoyue
 */

let auth = new Auth();//全局Auth对象

const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

class Homepage extends React.Component{


    static contextTypes = {
        router: PropTypes.object.isRequired
    };

    showLoginCard(){
         this.context.router.history.push("/login")
    }

    render(){

        return(
        <div className="page">
            <Router>
            <Layout style={{ height: '100%'}}>
                <Header className="header">
                    <div className="logo" />
                    <div className="loginPopover">
                            <Button type="primary" shape='circle' icon="login" onClick={()=>{this.context.router.history.push("/login")}}>
                            </Button>
                    </div>
                </Header>
                <Layout>
                    <Sider width={200} style={{ background: '#fff' }}>
                        <Menu
                            mode="inline"
                            defaultSelectedKeys={['0']}
                            style={{ height: '100%', borderRight: 0 }}
                        >
                            <Menu.Item key="0">
                                <Icon type="smile" theme="twoTone"/>
                                游客区
                                <Link to='/visitor'/>
                            </Menu.Item>
                            <Menu.Item key="1">
                                    <Icon type="smile" theme="twoTone"/>
                                    最热主题
                                    <Link to='/mosthottopic'/>
                                </Menu.Item>
                                <Menu.Item key="2">
                                    <Icon type="smile" theme="twoTone"/>
                                    最热留言
                                    <Link to='/hotmessagetopic'></Link>
                                </Menu.Item>
                                <Menu.Item key="3">
                                    <Icon type="smile" theme="twoTone"/>
                                    全部主题
                                    <Link to='/alltopic'></Link>
                                </Menu.Item>
                                <Menu.Item key="4">
                                    <Icon type="smile" theme="twoTone"/>
                                    我参与的
                                    <Link to='/involved'/>
                                </Menu.Item>

                        </Menu>
                    </Sider>
                    <Layout style={{ padding: '10px 10px 10px' }}>
                        <Content style={{ background: '#fff', padding: 24, margin: 0, minHeight: 280 }}>
                            <Route path='/visitor' component={Visitor}/>
                            <Route path='/mosthottopic' render={(props)=>{
                                if (auth.isLogin){
                                    return <MostHotTopic {...props}/>
                                }else{
                                    return <Redirect to='/login'/>
                                }
                            }}/>
                            <Route path='/hotmessagetopic' render={(props)=>{
                                if (auth.isLogin){
                                    return <HotMessageTopic {...props}/>
                                } else {
                                    return <Redirect to='/login'/>
                                }
                            }}/>
                            <Route path='/alltopic' render={(props)=>{
                                if (auth.isLogin){
                                    return <AllTopic {...props}/>
                                } else {
                                    return <Redirect to='/login'/>
                                }
                            }}/>
                            <Route path='/involved' render={(props) => {
                                if (auth.isLogin){
                                    return <Involved {...props}/>
                                } else {
                                    return <Redirect to='/login'/>
                                }
                            }}/>
                            <Route path='/login' render={(props)=>{
                                return <LoginForm auth={auth} {...props}/>
                            }}/>
                        </Content>
                    </Layout>
                </Layout>
            </Layout>

            </Router>
        </div>
        )
    }
}


export default Homepage;
