import React,{ Component } from 'react';
import axios from 'axios';
import { Button, Card, Row, Form, Icon, Input, Checkbox, Modal, Radio, Alert } from 'antd';
import './login.css';

/**
 * author: Chaoyue
 * login card
 */

const FormItem = Form.Item;

const CollectionCreateForm = Form.create()(//注册表单
    class extends React.Component {

        state = {
            confirmDirty: false,
            registerLoading: false,
            alertVisible: false
        };

        register = (username, password, email) =>{
            axios.get('http://127.0.0.1:8080/register',{
                params:{
                    username: username,
                    password: password,
                    email: email
                }
            })
                .then(function (response) {
                    this.setState({ registerLoading: false})
                    if (response === 1){

                    }
                    console.log(response)
                })
                .catch(function (error) {
                    console.log(error)
                    this.setState({ registerLoading: false})
                    this.setState({ alertVisible: true})
                })
        }

        handleSubmit = (e) => {
            e.preventDefault();
            this.props.form.validateFieldsAndScroll((err, values) => {
                if (!err) {
                    this.setState({ registerLoading: true})
                    console.log('Received values of form: ', values);//处理表单内容
                }
            });
        }
        handleConfirmBlur = (e) => {
            const value = e.target.value;
            this.setState({ confirmDirty: this.state.confirmDirty || !!value });
        }

        compareToFirstPassword = (rule, value, callback) => {
            const form = this.props.form;
            if (value && value !== form.getFieldValue('password')) {
                callback('输入密码不一致');
            } else {
                callback();
            }
        }

        validateToNextPassword = (rule, value, callback) => {
            const form = this.props.form;
            if (value && this.state.confirmDirty) {
                form.validateFields(['confirm'], { force: true });
            }
            callback();
        }

        handleAlertClose = () => {
            this.setState({ alertVisible: false });
        }


        render() {
            const { visible, onCancel, onCreate, form } = this.props;
            const { getFieldDecorator } = form;
            return (
                <Modal
                    visible={visible}
                    title="注册"
                    onCancel={onCancel}
                    footer={null}
                >
                    <Form layout="vertical" onSubmit={this.handleSubmit}>
                        {
                            this.state.alertVisible ? (
                                <Alert
                                    message="Alert Message Text"
                                    type="error"
                                    closable
                                    afterClose={this.handleAlertClose}
                                />
                            ) : null
                        }
                        <FormItem label="用户名">
                            {getFieldDecorator('username', {
                                rules: [{ required: true, message: '请输入用户名' }],
                            })(
                                <Input />
                            )}
                        </FormItem>
                        <FormItem
                            label="请输入密码"
                        >
                            {getFieldDecorator('password', {
                                rules: [{
                                    required: true, message: '请输入密码',
                                }, {
                                    validator: this.validateToNextPassword,
                                }],
                            })(
                                <Input type="password" />
                            )}
                        </FormItem>
                        <FormItem
                            label="请确认密码"
                        >
                            {getFieldDecorator('confirm', {
                                rules: [{
                                    required: true, message: '请确认输入的密码',
                                }, {
                                    validator: this.compareToFirstPassword,
                                }],
                            })(
                                <Input type="password" onBlur={this.handleConfirmBlur} />
                            )}
                        </FormItem>
                        <FormItem
                            label="请输入邮箱地址"
                        >
                            {getFieldDecorator('email', {
                                rules: [{
                                    type: 'email', message: '请输入正确邮箱!',
                                }, {
                                    required: true, message: '请输入邮箱',
                                }],
                            })(
                                <Input />
                            )}
                        </FormItem>
                        <FormItem className="collection-create-form_last-form-item">
                            {getFieldDecorator('modifier', {
                                initialValue: 'publicUser',
                            })(
                                <Radio.Group>
                                    <Radio value="publicUser">普通用户</Radio>
                                    <Radio value="adm">管理员</Radio>
                                </Radio.Group>
                            )}
                        </FormItem>
                        <FormItem>
                            <Button type="primary" htmlType="submit" className="registerButton" loading={this.state.registerLoading}>注册</Button>
                        </FormItem>
                    </Form>
                </Modal>
            );
        }
    }
);

class LoginCard extends React.Component{

    state={
        loading: false,
        visible: false,//注册modal可见性
    };

    handleSubmit = (e) => {//需要改
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);//处理登录数据
            }
        });
    }

    showRegisterModal = () =>{//显示注册表单
        this.setState({visible: true})
    }

    handleCancel = () => {
        this.setState({ visible: false });
    }

    handleCreate = () => {
        const form = this.formRef.props.form;
        form.validateFields((err, values) => {
            if (err) {
                return;
            }
            console.log('Received values of form: ', values);
            form.resetFields();
            this.setState({ visible: false });
        });
    }
    saveFormRef = (formRef) => {
        this.formRef = formRef;
    }

    render(){

        const { form} = this.props;//这里注意
        const { getFieldDecorator } = form;
            return (
                <div className="loginPage" style={{ background: '#fafafa' }}>

                    <Card className="loginCard" title="登录" style={{ width: 450}} >
                        <Form onSubmit={this.handleSubmit} className="login-form" >
                            <FormItem>
                                {getFieldDecorator('userName', {
                                    rules:[{ required: true, message: '请输入用户名靴靴'}]
                                })(
                                    <Input prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)'}} />} placeholder="用户名"/>
                                )}
                            </FormItem>
                            <FormItem>
                                {getFieldDecorator('password', {
                                    rules: [{required: true, message: '请输入你的密码'}],
                                })(
                                    <Input prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} type="password" placeholder="密码" />
                                )}
                            </FormItem>
                            <FormItem>
                                {getFieldDecorator('remember', {
                                    valuePropName: 'checked',
                                    initialValue: false,
                                })(
                                    <Checkbox>记住我</Checkbox>
                                )}
                                <a className="login-form-forgot" href="">忘记密码</a>
                                <Button type="primary" htmlType="submit" className="login-form-button">
                                    登录
                                </Button>
                                Or <a onClick={this.showRegisterModal}>快来注册</a>
                            </FormItem>
                        </Form>
                    </Card>
                    <CollectionCreateForm
                        wrappedComponentRef={this.saveFormRef}
                        visible={this.state.visible}
                        onCancel={this.handleCancel}
                        onCreate={this.handleCreate}
                    />
                </div>
            );
    }
}

const LoginForm = Form.create()(LoginCard);//这一句一定要加！！！

export default LoginForm;
