import React, { Component } from 'react';
import { List, Avatar, Icon } from 'antd'
import axios from "axios";

const IconText = ({ type, text }) => (
    <span>
    <Icon type={type} style={{ marginRight: 8 }} />
        {text}
  </span>
);

class HotMessageTopic extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            topics :[]
        }
    }

    componentWillMount(){
        this.getAllTopic()
    }

    getAllTopic(){
        let _this = this;
        axios.get("http://127.0.0.1:8080/getAllTopics")
            .then(function (reponse) {
                _this.setState({
                    topics:reponse.data
                });
                console.log(reponse.data)
            })
    }

    openOneTopic(){
        alert("hahahah")
    }

    render() {
        return (
            <div>
                <List
                    itemLayout="vertical"
                    dataSource={this.state.topics}
                    size="middle"
                    pagination={{
                        pageSize:4
                    }}
                    renderItem={item => (
                        <List.Item
                            key = {item.id}
                            actions={[<IconText type='star-o' text='0'/>, <IconText type='like-o' text='0'/>, <IconText type='message' text='0'/>]}
                        >
                            <List.Item.Meta
                                title={<a onClick={this.openOneTopic}>{item.title}</a>}
                                description = {[<Avatar
                                    shape={"square"}
                                    size={"small"}
                                    src={"https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"} />,
                                    item.username,
                                    ,"        ",item.time]}/>
                            {item.content}
                        </List.Item>
                    )}/>
            </div>
        );
    }
}
export default HotMessageTopic;
