import React, { Component } from 'react';
import { Card, Avatar } from 'antd';
import axios from 'axios';
import './AllTopic.css'

const { Meta } = Card;

const IconText = ({ type, text }) => (
    <span>
    <Icon type={type} style={{ marginRight: 8 }} />
        {text}
  </span>
);


class AllTopic extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            topics :[]
        }
    }

    componentWillMount(){
        this.getAllTopic()
    }

    getAllTopic(){
        let _this = this;
        axios.get("http://127.0.0.1:8080/getAllTopics")
            .then(function (reponse) {
                _this.setState({
                    topics:reponse.data
                });
                console.log(reponse.data)
            })
    }

    render() {
        return (
            <div>
                {this.state.topics.map((topic) => <TopicCard topic={topic} /> )}
            </div>
        );
    }
}

export default AllTopic;
