import React, { Component } from 'react';
import { Modal, Input, Tag, Button, Form, Select} from 'antd'

const FormItem = Form.Item;
const { TextArea } = Input;
const Option = Select.Option;

class EditMessageForm extends React.Component{

    constructor(props){
        super(props);

        this.state={
            visible: false
        }
    }

    render() {

        const { editFormVisible, onCancel, onCreate, form } = this.props;
        const { getFieldDecorator } = form;

        return (
            <div>
                <Modal visible={editFormVisible}
                       title={"新建主题"}
                       mask={false}
                       onOk={this.handleOk}
                       onCancel={this.handleCancel}
                       footer={[
                           <Button key={"back"} onClick={this.handleCancel}>取消</Button>
                           ,<Button key={"submit"} type={"primary"} onClick={this.submitMessage}>提交</Button>
                       ]}>
                <Form layout={"vertical"}>
                    <FormItem label="标题">
                        {getFieldDecorator('title', {
                            rules: [{ required: true, message: '请输入标题' }],
                        })(
                            <Input />
                        )}
                    </FormItem>
                    <FormItem label="内容">
                        {getFieldDecorator("content"),(<TextArea rows={4} />)}
                    </FormItem>
                    <FormItem label="选择结点">
                        {getFieldDecorator("select"),(
                            <Select placeholder={"选择发布结点"}>
                                <Option value="campus">校园</Option>
                                <Option value="computer">计算机</Option>
                                <Option value="fun">好玩</Option>
                            </Select>
                        )}
                    </FormItem>
                </Form>
                </Modal>
            </div>
        );
    }
}

const EditMessage = Form.create()(EditMessageForm);

export default EditMessage;
