import React, { Component } from 'react';
import {Drawer} from 'antd';
import axios from "axios";


class TopicInfo extends React.Component{

    constructor(props){

        super(props);
        this.state = {
            topicinfo:[],
            comments: []
        }
    }

    getTopicInfo(topicid){

        let _this = this;
        axios.get("http://127.0.0.1:8080/getonetopicinfo",{
            params:{
                topicid:topicid
            }
        }).then(function (response) {
            _this.setState({
                topicinfo:response.data
            })
        })
    }

    getTopicComments(topicid){

        let _this = this;
        axios.get("http://127.0.0.1:8080/gettopiccomments",{
            params:{
                topicid: topicid
            }
        }).then(function (response) {
            _this.setState({
                comments: response.data
            })
        })
    }

    componentWillMount(){
        this.getTopicInfo(this.props.topicid);
        this.getTopicComments(this.props.topicid);
    }

    render() {

        return (
            <div>
                <Drawer
                    width={600}
                    placement={"right"}
                    visible={this.props.topicDrawerVisible}
                    onClose={() => this.props.closeTopicDrawer(false)}
                    title={this.state.topicinfo.title}
                    >

                </Drawer>
            </div>
        );
    }
}

export default TopicInfo;
