import React from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import {Row, Layout,Menu, Breadcrumb, Icon} from 'antd'
import App from "./App";
import './homepage.css'
import MostHotTopic from "./MostHotTopic";
import HotMessageTopic from "./HotMessageTopic";
import AllTopic from "./AllTopic";
import Involved from "./Involved";

/**
 * created by Chaoyue
 */

const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

class Homepage extends React.Component{

    render(){
        return(
        <div className="page">
            <Layout style={{ height: '100%'}}>
                <Header className="header">
                    <div className="logo" />
                </Header>
                <Layout>
                    <Router>
                    <Sider width={200} style={{ background: '#fff' }}>
                        <Menu
                            mode="inline"
                            defaultSelectedKeys={['1']}
                            defaultOpenKeys={['sub1']}
                            style={{ height: '100%', borderRight: 0 }}
                        >
                                <Menu.Item key="1">
                                    <Icon type="smile" theme="twoTone"/>
                                    <Link to='/mosthottopic'>最热主题</Link>
                                </Menu.Item>
                                <Menu.Item key="2">
                                    <Icon type="smile" theme="twoTone"/>
                                    <Link to='/hotmessagetopic'>最热留言</Link>
                                </Menu.Item>
                                <Menu.Item key="3">全部主题</Menu.Item>
                                <Menu.Item key="4">我参与的</Menu.Item>

                        </Menu>
                    </Sider>
                    <Layout style={{ padding: '10px 10px 10px' }}>
                        <Content style={{ background: '#fff', padding: 24, margin: 0, minHeight: 280 }}>
                            Content

                            <div>
                                <Switch>
                                <Route path='/mosthottopic' component={MostHotTopic}/>
                                <Route path='/hotmessagetopic' component={HotMessageTopic}/>
                                <Route path='/alltopic' component={AllTopic}/>
                                <Route path='/involved' component={Involved}/>
                                </Switch>
                            </div>

                        </Content>
                    </Layout>
                    </Router>
                </Layout>
            </Layout>
        </div>
        )
    }
}


export default Homepage;
