import React, { Component } from 'react';
import { List, Avatar, Icon } from 'antd'
import axios from "axios";
import TopicInfo from "./TopicInfo";

const IconText = ({ type, text }) => (
    <span>
    <Icon type={type} style={{ marginRight: 8 }} />
        {text}
  </span>
);

class MostHotTopic extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            topics :[],
            topicDrawerVisible:false,
            focusId: null,
            oneTopicinfo:[],
            comments:[],
            title:""
        }
    }

    componentWillMount(){
        this.getAllTopic()
    }


    getAllTopic(){
        let _this = this;
        axios.get("http://127.0.0.1:8080/getAllTopics")
            .then(function (reponse) {
                _this.setState({
                    topics:reponse.data
                });
                console.log(reponse.data)
            })
    }

    openOneTopic(id){

        console.log("id = ", id);
        this.setState({
            topicDrawerVisible: true,
            focusId: id
        })
    }

    closeTopicDrawer(topicDrawerVisible){
        this.setState({
            topicDrawerVisible
        })
    }

    render() {
        return (
            <div>
                <List
                    itemLayout="vertical"
                    dataSource={this.state.topics}
                    size="middle"
                    pagination={{
                        pageSize:4
                    }}
                    renderItem={item => (
                        <List.Item
                            key = {item.id}
                            actions={[<IconText type='star-o' text='0'/>, <IconText type='like-o' text='0'/>, <IconText type='message' text='0'/>]}
                        >
                            <List.Item.Meta
                                title={<a onClick={()=> this.openOneTopic(item.id)}>{item.title}</a>}
                                description = {[<Avatar
                                    shape={"square"}
                                    size={"small"}
                                    src={"https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"} />,
                                    item.username,
                                    ,"        ",item.time]}/>
                            {item.content}
                        </List.Item>
                    )}/>
                <TopicInfo
                    topicDrawerVisible = {this.state.topicDrawerVisible}
                    closeTopicDrawer = { topicDrawerVisible => this.closeTopicDrawer(topicDrawerVisible)}
                    topicid = {this.state.focusId}
                    username = {this.props.username}
                />
            </div>
        );
    }
}

export default MostHotTopic;
