export default class Auth{

    constructor(){
        this.isLogin = false;
        this.username = null;
    }

    login(username, password, callback){

        if(username === 'chaoyue' && password ==='123'){
            this.isLogin = true;
            this.username = username;
            callback();
        }else  {
            alert("登录失败");
        }
    }
}
