import React, { Component } from 'react';
import { List, Avatar, Icon, message } from 'antd'
import axios from "axios";
import TopicInfo from "./TopicInfo";

const IconText = ({ type, text, submit}) => (
    <span>
        <a onClick={submit}>
            <Icon type={type} style={{ marginRight: 8 }} />
            {text}
        </a>
  </span>
);

class HotMessageTopic extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            topics :[],
            topicDrawerVisible:false,
            focusId: null,
            oneTopicinfo:[],
            comments:[],
            title:""
        }
    }

    componentWillMount(){
        this.getAllTopic()
    }


    getAllTopic(){
        let _this = this;
        axios.get("http://127.0.0.1:8080/getAllTopics")
            .then(function (reponse) {
                _this.setState({
                    topics:reponse.data
                });
                console.log(reponse.data)
            })
    }

    openOneTopic(id){

        console.log("id = ", id);
        this.setState({
            topicDrawerVisible: true,
            focusId: id
        })
    }

    closeTopicDrawer(topicDrawerVisible){
        this.setState({
            topicDrawerVisible
        })
    }

    collect(username, topicid){
        axios.get("http://127.0.0.1:8080/liketopic",{
            params:{
                username: username,
                topicid: topicid
            }
        }).then(function (response) {
           if (response.data == 1){
               message.success("点赞成功~！")
           }  else {
               message.warning("算了吧别点赞了")
           }
        }).catch(function (error) {
            console.log(error)
        })
    }

    render() {
        return (
            <div>
                <List
                    itemLayout="vertical"
                    dataSource={this.state.topics}
                    size="middle"
                    pagination={{
                        pageSize:4
                    }}
                    renderItem={item => (
                        <List.Item
                            key = {item.id}
                            actions={[<IconText type='star' text={item.collectionsNum} submit={this.collect(this.props,username, item.id)}/>, <IconText type='like-o' text={item.likesNum}/>, <IconText type='message' text={item.commentsNum}/>]}
                        >
                            <List.Item.Meta
                                title={<a onClick={()=> this.openOneTopic(item.id)}>{item.title}</a>}
                                description = {[<Avatar
                                    shape={"square"}
                                    size={"small"}
                                    src={"https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"} />,
                                    item.username,
                                    ,"        ",item.time]}/>
                            {item.content}
                        </List.Item>
                    )}/>
                <TopicInfo
                    topicDrawerVisible = {this.state.topicDrawerVisible}
                    closeTopicDrawer = { topicDrawerVisible => this.closeTopicDrawer(topicDrawerVisible)}
                    topicid = {this.state.focusId}
                    username = {this.props.username}
                />
            </div>
        );
    }
}
export default HotMessageTopic;
